mapper.select:
SELECT HO.SALE_ORDER_NO AS hotelOrderNo, CC.CUSTOMER_TYPE_NO AS customerType, CCT.NAME AS customerTypeName, HO.CUSTOMER_NO AS customerNo, CC.NAME AS customerName, HO.CREATE_ORDER_TIME AS createTime, HO.HOTEL_ADDRESS AS holAddr, HO.HOTEL_NAME AS hotelName, HO.GUEST_NAME AS guestName, HO.ARRIVAL_DATE AS arrivalDate, HO.DEPARTURE_DATE AS departureDate, HO.FACT_GUEST_NAME AS factGuestName, HO.FACT_ARRIVE_TIME AS factArrivalDate, HO.FACT_LEAVE_TIME AS factDepartureDate, HO.TC_ORDER_STATUS AS checkStatus, HO.ORDER_STATUS AS orderStatus, HO.CREATOR AS creator, HO.NIGHT_COUNT AS bookPro, HO.BOOK_COUNT AS bookNight, (HO.BOOK_COUNT * HO.NIGHT_COUNT) AS proNight, HO.TOTAL_PRICE AS totalPrice, HO.TOTAL_REFUND AS saleRefund, HO.TOTAL_REFUND / HO.TOTAL_PRICE AS rate, HO.FACT_NIGHT_COUNT AS factBookPro, HO.FACT_PRO_COUNT AS factBookNight, (HO.FACT_PRO_COUNT * HO.FACT_NIGHT_COUNT) AS factProNight, HO.FACT_TOTAL_PRICE AS factTotalPrice, HO.FACT_TOTAL_REFUND AS factSaleRefund, HO.FACT_TOTAL_REFUND / HO.FACT_TOTAL_PRICE AS factRate, C.BANK_NAME AS bankName, C.CARD_NO AS cardNo, C.USER_NAME AS userName, C.PROVINCE AS province, C.CITY AS bankCity, C.OPEN_BANK AS bankAddr FROM `gss-op`.HOL_HOTEL_ORDER AS HO LEFT JOIN `gss-op`.CPS_CUSTOMER AS CC ON (CC.CUSTOMER_NO = HO.CUSTOMER_NO) LEFT JOIN `gss-op`.CPS_BANK_CARD AS C ON (C.CUSTOMER_NO = HO.CUSTOMER_NO) LEFT JOIN `gss-op`.CPS_CUSTOMER_TYPE AS CCT ON (CCT.CUSTOMER_TYPE_NO = CC.CUSTOMER_TYPE_NO)
and 1=1
AND HO.HOTEL_ORDER_NO=#{hotelOrderNo,jdbcType=VARCHAR}
AND CC.CUSTOMER_TYPE_NO=#{customerType,jdbcType=BIGINT}
AND CCT.NAME=#{customerTypeName,jdbcType=VARCHAR}
AND HO.CUSTOMER_NO=#{customerNo,jdbcType=BIGINT}
AND CC.NAME=#{customerName,jdbcType=VARCHAR}
AND HO.HOTEL_ADDRESS=#{holAddr,jdbcType=VARCHAR}
AND HO.HOTEL_NAME LIKE CONCAT(CONCAT('%', #{hotelName}), '%')
AND HO.GUEST_NAME=#{guestName,jdbcType=VARCHAR}
AND HO.CREATE_ORDER_TIME >=#{startDate,jdbcType=TIMESTAMP}
AND HO.ARRIVAL_DATE >=#{startDate,jdbcType=TIMESTAMP}
AND HO.DEPARTURE_DATE >=#{startDate,jdbcType=TIMESTAMP}
AND HO.CREATE_ORDER_TIME <=#{endDate,jdbcType=TIMESTAMP}
AND HO.ARRIVAL_DATE <=#{endDate,jdbcType=TIMESTAMP}
AND HO.DEPARTURE_DATE <=#{endDate,jdbcType=TIMESTAMP}
AND HO.BOOK_COUNT=#{bookPro,jdbcType=TINYINT}
AND HO.NIGHT_COUNT=#{bookNight,jdbcType=TINYINT}
AND HO.TC_ORDER_STATUS=#{checkStatus,jdbcType=VARCHAR}
AND HO.CREATOR=#{creator,jdbcType=VARCHAR}
AND HO.ORDER_STATUS IN(701,800,801)
ORDER BY HO.CREATE_ORDER_TIME DESC

SELECT
HO.HOTEL_NAME AS hotelName,
count(HO.HOTEL_ORDER_NO) AS orderCount,
HO.HOTEL_ADDRESS AS holAddr,
sum(HO.FACT_PRO_COUNT) AS bookPro,
sum(HO.FACT_NIGHT_COUNT * HO.NIGHT_COUNT) AS proNight,
sum(HO.FACT_TOTAL_PRICE) AS totalPrice,
sum(HO.FACT_TOTAL_REFUND) AS saleRefund,
sum((HS.RATE / 100) * HO.TOTAL_PRICE) AS gProfit,
sum(((HS.RATE / 100 * HO.TOTAL_PRICE) - HO.FACT_TOTAL_REFUND)) AS profit
FROM `gss-op`.HOL_HOTEL_ORDER AS HO
LEFT JOIN `gss-op`.CPS_CUSTOMER AS CC ON(CC.CUSTOMER_NO=HO.CUSTOMER_NO)
LEFT JOIN `gss-op`.CPS_CUSTOMER_TYPE AS CCT ON(CCT.CUSTOMER_TYPE_NO=CC.CUSTOMER_TYPE_NO)
LEFT JOIN `gss-op`.HOL_SUPPLIER AS HS ON (HS.SUPPLIERNO = HO.SUPPLIER_NO)

    and 1=1

        AND HO.HOTEL_ORDER_NO=#{hotelOrderNo,jdbcType=VARCHAR}


        AND CC.CUSTOMER_TYPE_NO=#{customerType,jdbcType=BIGINT}


        AND CCT.NAME=#{customerTypeName,jdbcType=VARCHAR}


        AND HO.CUSTOMER_NO=#{customerNo,jdbcType=BIGINT}


        AND CC.NAME=#{customerName,jdbcType=VARCHAR}



        AND HO.HOTEL_ADDRESS=#{holAddr,jdbcType=VARCHAR}


        AND HO.HOTEL_NAME LIKE CONCAT(CONCAT('%', #{hotelName}), '%')


        AND HO.GUEST_NAME=#{guestName,jdbcType=VARCHAR}



            AND HO.CREATE_ORDER_TIME >=#{startDate,jdbcType=TIMESTAMP}


            AND HO.ARRIVAL_DATE >=#{startDate,jdbcType=TIMESTAMP}


            AND HO.DEPARTURE_DATE >=#{startDate,jdbcType=TIMESTAMP}




            AND HO.CREATE_ORDER_TIME <=#{endDate,jdbcType=TIMESTAMP}


            AND HO.ARRIVAL_DATE <=#{endDate,jdbcType=TIMESTAMP}


            AND HO.DEPARTURE_DATE <=#{endDate,jdbcType=TIMESTAMP}



        AND HO.BOOK_COUNT=#{bookPro,jdbcType=TINYINT}


        AND HO.NIGHT_COUNT=#{bookNight,jdbcType=TINYINT}


        AND HO.TC_ORDER_STATUS=#{checkStatus,jdbcType=VARCHAR}


        AND HO.CREATOR=#{creator,jdbcType=VARCHAR}


GROUP BY HO.HOTEL_CODE
ORDER BY totalPrice DESC

SELECT
CC.NAME AS customerName,
CCT.NAME AS customerTypeName,
COUNT(HO.HOTEL_ORDER_NO) AS orderCount,
sum(HO.FACT_PRO_COUNT) AS bookPro,
sum(HO.FACT_NIGHT_COUNT * HO.NIGHT_COUNT) AS proNight,
sum(HO.FACT_TOTAL_PRICE) AS totalPrice,
sum(HO.FACT_TOTAL_REFUND) AS saleRefund,
sum((HS.RATE / 100) * HO.TOTAL_PRICE) AS gProfit,
CC.CUSTOMER_TYPE_NO AS customerType,
sum(((HS.RATE / 100 * HO.TOTAL_PRICE) - HO.FACT_TOTAL_REFUND)) AS profit
FROM `gss-op`.HOL_HOTEL_ORDER AS HO
LEFT JOIN `gss-op`.CPS_CUSTOMER AS CC ON (CC.CUSTOMER_NO = HO.CUSTOMER_NO)
LEFT JOIN `gss-op`.CPS_CUSTOMER_TYPE AS CCT ON (CCT.CUSTOMER_TYPE_NO = CC.CUSTOMER_TYPE_NO)
LEFT JOIN `gss-op`.HOL_SUPPLIER AS HS ON (HS.SUPPLIERNO = HO.SUPPLIER_NO)

    and 1=1

        AND HO.HOTEL_ORDER_NO=#{hotelOrderNo,jdbcType=VARCHAR}


        AND CC.CUSTOMER_TYPE_NO=#{customerType,jdbcType=BIGINT}


        AND CCT.NAME=#{customerTypeName,jdbcType=VARCHAR}


        AND HO.CUSTOMER_NO=#{customerNo,jdbcType=BIGINT}


        AND CC.NAME=#{customerName,jdbcType=VARCHAR}



        AND HO.HOTEL_ADDRESS=#{holAddr,jdbcType=VARCHAR}


        AND HO.HOTEL_NAME LIKE CONCAT(CONCAT('%', #{hotelName}), '%')


        AND HO.GUEST_NAME=#{guestName,jdbcType=VARCHAR}



            AND HO.CREATE_ORDER_TIME >=#{startDate,jdbcType=TIMESTAMP}


            AND HO.ARRIVAL_DATE >=#{startDate,jdbcType=TIMESTAMP}


            AND HO.DEPARTURE_DATE >=#{startDate,jdbcType=TIMESTAMP}




            AND HO.CREATE_ORDER_TIME <=#{endDate,jdbcType=TIMESTAMP}


            AND HO.ARRIVAL_DATE <=#{endDate,jdbcType=TIMESTAMP}


            AND HO.DEPARTURE_DATE <=#{endDate,jdbcType=TIMESTAMP}



        AND HO.BOOK_COUNT=#{bookPro,jdbcType=TINYINT}


        AND HO.NIGHT_COUNT=#{bookNight,jdbcType=TINYINT}


        AND HO.TC_ORDER_STATUS=#{checkStatus,jdbcType=VARCHAR}


        AND HO.CREATOR=#{creator,jdbcType=VARCHAR}



    GROUP BY HO.CUSTOMER_NO


    GROUP BY CC.CUSTOMER_TYPE_NO,HO.CUSTOMER_NO

ORDER BY totalPrice DESC
