mapper.update:
update HOL_HOTEL_ORDER
set ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR},
MODIFIER = #{modifier,jdbcType=VARCHAR},
MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
where SALE_ORDER_NO = #{saleOrderNo,jdbcType=BIGINT}

mapper.select:
select hho.ID, hho.HOTEL_ORDER_NO, hho.SALE_ORDER_NO, hho.BUY_ORDER_NO, hho.HOTEL_CODE, hho.HOTEL_NAME, hho.ORDER_TYPE, hho.CONTACT_NAME, hho.CONTACT_NUMBER, hho.CANCEL_TIME, hho.ARRIVAL_DATE, hho.DEPARTURE_DATE, hho.TOTAL_PRICE, hho.TOTAL_REFUND, hho.GUEST_NAME, hho.REMARK, hho.LOCKER, hho.LOCK_TIME, hho.CREATOR, hho.CREATE_ORDER_TIME, hho.MODIFIER, hho.MODIFY_TIME, hho.PRODUCT_UNIQUE_ID, hho.BOOK_COUNT, hho.PAYMENT_SIGN, hho.BANK_ID, hho.BANK_NAME, hho.CARD_NUMBER, hho.CARD_USER_NAME, hho.CARD_VALUE_DATE, hho.CREDENTIAL_NAME, hho.CREDENTIAL_NUMBER, hho.CARD_MOBILE, hho.ORDER_STATUS, hho.MSG, hho.IS_AFFIRM, hho.RESULT_CODE, hho.NIGHT_COUNT, hho.OWNER, hho.PRO_ID, hho.HOTEL_ADDRESS, hho.HOTEL_PHONE, hho.PRO_NAME, hho.TC_ORDER_STATUS, hho.TRANSATION_ORDER_NO, hho.GUEST_MOBILE, hho.DB_ORDER_TYPE, hho.DB_ORDER_MONEY, hho.DB_CANCEL_RULE, hho.CANCEL_PENALTY, hho.EARLY_ARRIVE_TIME, hho.LATEST_ARRIVE_TIME, hho.CUSTOMER_NO, hho.SUPPLIER_NO, hho.CARD_VALUE_CODE, hho.ARRIVE_HOTEL_TIME, hho.REQUEST_CODE, hho.REQUEST_TEXT, hho.REQUEST_NAME, hho.FACT_ARRIVE_TIME, hho.FACT_LEAVE_TIME, hho.FACT_GUEST_NAME, hho.FACT_PRO_COUNT, hho.FACT_NIGHT_COUNT, hho.EACH_NIGHT_PRICE, hho.FACT_TOTAL_PRICE, hho.BREAKFAST_COUNT, hho.SUPPLIER_NUMBER, CASE WHEN TIMESTAMPDIFF(MINUTE, hho.CREATE_ORDER_TIME, NOW()) >= 5 AND hho.order_status = '100' THEN 1 WHEN TIMESTAMPDIFF(MINUTE, hho.CREATE_ORDER_TIME, NOW()) >= 5 AND hho.order_status = '111' THEN 1 WHEN TIMESTAMPDIFF(MINUTE, hho.CREATE_ORDER_TIME, NOW()) >= 5 AND hho.order_status = '101' THEN 1 WHEN TIMESTAMPDIFF(MINUTE, hho.CREATE_ORDER_TIME, NOW()) >= 5 AND hho.order_status = '666' THEN 1 ELSE 0 END as BED_TYPE_NAME
FROM HOL_HOTEL_ORDER hho, OS_SALEORDER os where hho.SALE_ORDER_NO = os.SALE_ORDER_NO
AND hho.OWNER = #{owner,jdbcType=INTEGER}
AND os.CUSTOMER_NO = #{customerNo,jdbcType=BIGINT}
AND hho.SALE_ORDER_NO = #{saleOrderNo,jdbcType=BIGINT}
AND os.TRANSATION_ORDER_NO = #{transactionNo,jdbcType=BIGINT}
AND hho.LOCKER = #{locker,jdbcType=BIGINT}
AND hho.CREATE_ORDER_TIME BETWEEN #{createStartTime} AND #{createEndTime}
AND hho.ARRIVAL_DATE BETWEEN #{arrivalStartDate} AND #{arrivalEndDate}
AND hho.DEPARTURE_DATE BETWEEN #{departureStartDate} AND #{departureEndDate}
AND hho.HOTEL_NAME LIKE CONCAT(CONCAT('%', #{hotelName}), '%')
AND hho.GUEST_NAME LIKE CONCAT(CONCAT('%', #{guestName}), '%')
AND hho.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
AND hho.HOTEL_ORDER_NO = #{hotelOrderNo,jdbcType=VARCHAR}
AND hho.RESULT_CODE = #{resultCode,jdbcType=VARCHAR}
AND hho.CONTACT_NAME = #{contactName,jdbcType=VARCHAR}
AND hho.SUPPLIER_NUMBER = #{supplierNumber,jdbcType=VARCHAR}
AND hho.TC_ORDER_STATUS = #{supplierOrderStatus,jdbcType=VARCHAR}
AND hho.HOTEL_ADDRESS LIKE CONCAT(CONCAT('%', #{cityName}), '%')
ORDER BY CREATE_ORDER_TIME DESC

select DISTINCT hho.ID, hho.HOTEL_ORDER_NO, hho.SALE_ORDER_NO, hho.BUY_ORDER_NO, hho.HOTEL_CODE, hho.HOTEL_NAME, hho.ORDER_TYPE, hho.CONTACT_NAME, hho.CONTACT_NUMBER, hho.CANCEL_TIME, hho.ARRIVAL_DATE, hho.DEPARTURE_DATE, hho.TOTAL_PRICE, hho.TOTAL_REFUND, hho.GUEST_NAME, hho.REMARK, hho.LOCKER, hho.LOCK_TIME, hho.CREATOR, hho.CREATE_ORDER_TIME, hho.MODIFIER, hho.MODIFY_TIME, hho.PRODUCT_UNIQUE_ID, hho.BOOK_COUNT, hho.PAYMENT_SIGN, hho.BANK_ID, hho.BANK_NAME, hho.CARD_NUMBER, hho.CARD_USER_NAME, hho.CARD_VALUE_DATE, hho.CREDENTIAL_NAME, hho.CREDENTIAL_NUMBER, hho.CARD_MOBILE, hho.ORDER_STATUS, hho.MSG, hho.IS_AFFIRM, hho.RESULT_CODE, hho.NIGHT_COUNT, hho.OWNER, hho.PRO_ID, hho.HOTEL_ADDRESS, hho.HOTEL_PHONE, hho.PRO_NAME, hho.TC_ORDER_STATUS, hho.TRANSATION_ORDER_NO, hho.GUEST_MOBILE, hho.DB_ORDER_TYPE, hho.DB_ORDER_MONEY, hho.DB_CANCEL_RULE, hho.CANCEL_PENALTY, hho.EARLY_ARRIVE_TIME, hho.LATEST_ARRIVE_TIME, hho.CUSTOMER_NO, hho.SUPPLIER_NO, hho.CARD_VALUE_CODE, hho.ARRIVE_HOTEL_TIME, hho.REQUEST_CODE, hho.REQUEST_TEXT, hho.REQUEST_NAME, hho.FACT_ARRIVE_TIME, hho.FACT_LEAVE_TIME, hho.FACT_GUEST_NAME, hho.FACT_PRO_COUNT, hho.FACT_NIGHT_COUNT, hho.EACH_NIGHT_PRICE, hho.FACT_TOTAL_PRICE, hho.BREAKFAST_COUNT, hho.SUPPLIER_NUMBER FROM HOL_HOTEL_ORDER hho, OS_SALEORDER os where hho.SALE_ORDER_NO = os.SALE_ORDER_NO
AND hho.OWNER = #{owner,jdbcType=INTEGER}
AND hho.CREATOR = #{creator,jdbcType=VARCHAR}
AND hho.SALE_ORDER_NO = #{saleOrderNo,jdbcType=BIGINT}
AND os.TRANSATION_ORDER_NO = #{transactionNo,jdbcType=BIGINT}
AND date(hho.CREATE_ORDER_TIME) >= date(#{createStartTime,jdbcType=DATE})
AND date(hho.CREATE_ORDER_TIME) <= date(#{createEndTime,jdbcType=DATE})
AND date(hho.ARRIVAL_DATE) >= date(#{arrivalStartDate,jdbcType=DATE})
AND date(hho.ARRIVAL_DATE) <= date(#{arrivalEndDate,jdbcType=DATE})
AND date(hho.DEPARTURE_DATE) >= date(#{departureStartDate,jdbcType=DATE})
AND date(hho.DEPARTURE_DATE) <= date(#{departureEndDate,jdbcType=DATE})
AND hho.HOTEL_NAME LIKE CONCAT(CONCAT('%', #{hotelName}), '%')
AND hho.GUEST_NAME LIKE CONCAT(CONCAT('%', #{guestName}), '%')
AND hho.CUSTOMER_NO IN
#{item}
AND hho.ORDER_STATUS = #{orderStatus,jdbcType=VARCHAR}
AND hho.HOTEL_ORDER_NO = #{hotelOrderNo,jdbcType=VARCHAR}
AND hho.RESULT_CODE = #{resultCode,jdbcType=VARCHAR}
AND hho.CONTACT_NAME = #{contactName,jdbcType=VARCHAR}
ORDER BY CREATE_ORDER_TIME DESC
