mapper.select:
select
from ( SELECT OS. OWNER AS OWNER, so. NAME AS SALE_COM, so. NAME AS ACC_COM, 2 AS PRO_TYPE, OS.SOURCE_CHANNEL_NO AS LINE, SMT.NAME AS SALE_DEPT, OS.CUSTOMER_NO AS CUS_CODE, OSE.CUSTOMER_TYPE_NAME AS CUS_TYPE, OSE.CUSTOMER_NO_NAME AS CUS_NAME, OS.SALE_ORDER_NO AS SALE_ORD_NUM, '' AS PRO_ORD_NUM, group_concat( VBSN.SETTLEMENT_NO SEPARATOR ',' ) AS BILL_NUM, OS.SOURCE_CHANNEL_NO AS SALE_SOURCE, OS.ORDERING_TIME AS ISSUE_DATE, VISB.DEP_TIME AS FLIGHT_DATE, VISB.AIRLINE AS AIR_COM, LEFT (VISB.TICKET_NO, 3) AS AIR_CODE, RIGHT (VISB.TICKET_NO, 10) AS TICKET_NO, VISB.ROUTING AS ROUTING, VISB.FLIGHT_NO AS FLIGHT, VISB.CABIN AS CABIN, VISB.PASS_NAME AS PASS_NAME, VISB.PNR AS PNR, '' AS RATE, VISB.SALE_FARE AS TIC_PRICE, VISB.SALE_TAX AS TAX, VISB.SALE_AWARD_PRICE AS COMMISSION, VISB.SALE_AGENCY_FEE AS SER_CHARGE, VISB.SALE_PRICE AS RECE_AMOUNT, VISB.BUY_PRICE AS PAY_AMOUNT, VISB.BUY_PRICE AS PAID_AMOUNT, PPW. NAME AS SUPP_PAY_MODE, PAPI.THREE_TRADE_NO AS SUPP_PAY_NUM, CS.OFFICE_NO AS OFFICE, CS. NAME AS SUPPLIER_NAME, ( VISB.SALE_PRICE - VISB.BUY_PRICE ) AS PROFIT, VISBR.CREATE_TIME AS REFUND_DATE, VISBR.SALE_BROKERAGE AS REFUND_FEE, VISBR.SALE_REFUND_PRICE AS REFUND_AMOUNT, VISBR.BUY_BROKERAGE AS PAY_SUPP_FEE, VISBR.SALE_REFUND_PRICE AS REAL_AMOUNT, '' AS TRIP_NUM, VISB.CUS_PEO AS CUS_PEO, VISB.TIC_PEO AS TIC_PEO, SMD.NAME AS TICKETDEP,
IF ( VISBR.TRANSATION_ORDER_NO, '退票',
IF ( VISBC.TRANSATION_ORDER_NO, '改期', '出票' ) ) AS TIC_STATUS FROM ( ( ( ( ( ( ( ( ( ( V_IFT_SALE_BUY VISB LEFT JOIN OS_SALEORDER OS ON ( ( VISB.SALE_ORDER_NO = OS.SALE_ORDER_NO ) ) ) LEFT JOIN OS_SALEORDER_EXTEND OSE ON ( ( OSE.SALE_ORDER_NO = OS.SALE_ORDER_NO ) ) ) LEFT JOIN OS_ACTUALAMOUNTRECORD OA ON ( ( ( OA.RECORD_NO = VISB.BUY_ORDER_NO ) AND (OA.BUSINESS_TYPE = 3) ) ) ) LEFT JOIN PS_ALL_PAY_INFO PAPI ON ( ( PAPI.ALL_PAY_INFO_NO = OA.PAY_NO ) ) ) LEFT JOIN PS_PAY_WAY PPW ON ( (PPW. CODE = PAPI.PAY_WAY_CODE) ) ) LEFT JOIN CPS_SUPPLIER CS ON ( ( CS.SUPPLIER_NO = VISB.SUPPLIER_NO ) ) ) LEFT JOIN V_IFT_SALE_BUY_REFUND VISBR ON ( ( ( VISBR.TRANSATION_ORDER_NO = VISB.TRANSATION_ORDER_NO ) AND ( VISBR.TICKET_NO = VISB.TICKET_NO ) ) ) ) LEFT JOIN V_IFT_SALE_BUY_CHANGE VISBC ON ( ( ( VISBC.TRANSATION_ORDER_NO = VISB.TRANSATION_ORDER_NO ) AND ( VISBC.TICKET_NO = VISB.TICKET_NO ) ) ) ) LEFT JOIN SM_OWNER_INFO so ON ((so. OWNER = OS. OWNER)) LEFT JOIN SM_USER SU ON ((SU.LOGIN_NAME=VISB.TIC_PEO)) LEFT JOIN SM_USER SUR ON ((SUR.LOGIN_NAME=VISB.CUS_PEO)) LEFT JOIN SM_DEPT SMD ON SMD.ID=SU.DEPT_ID LEFT JOIN SM_DEPT SMT ON SMT.ID=SUR.DEPT_ID ) LEFT JOIN V_BUSINESS_SETTLEMENT_NO VBSN ON ( ( ( VBSN.RECORD_NO = OS.SALE_ORDER_NO ) AND (VBSN.BUSINESS_TYPE = 2) ) ) ) GROUP BY OS.SALE_ORDER_NO, VBSN.RECORD_NO, VBSN.BUSINESS_TYPE ) ifr where 1=1
AND ifr.OWNER = #{OWNER}
AND ifr.PRO_TYPE = #{proType}
AND ifr.CUS_NAME = #{cusName}
AND ifr.CUS_CODE = #{cusCode}
AND ifr.SALE_ORD_NUM = #{saleOrdNum}
AND ifr.ISSUE_DATE >= str_to_date(concat(#{issueStartDate}," 00:00:00"), "%Y-%m-%d %T")
AND ifr.ISSUE_DATE <= str_to_date(concat(#{issueEndDate}," 23:59:59"), "%Y-%m-%d %T")
AND ifr.PNR = #{pnr}
AND ifr.BILL_NUM = #{billNum}
