mapper.select:
SELECT OC.CHILD_STATUS AS childStatus, CSP.`NAME` AS supplierName, ICE.CHANGE_TYPE AS type, ISOE.ISSUE_TIME AS issueTime, OSO.SALE_ORDER_NO AS saleOrderNo, OC.SALE_CHANGE_NO AS saleChangeNo, OSO.MODIFIER AS saler, OC.MODIFIER AS refunder, DEPT1. NAME AS dep, DEPT2. NAME AS ticketDep, CUS. NAME AS customerName, CUS.BALANCE_CORPORATION AS customerCompany, OSO.SOURCE_CHANNEL_NO AS sourceChannel, CER.PAY_WAY AS settleWay, OSO.CUSTOMER_NO AS customerNo, ( CASE CER.SETTLEMENT_STATUS WHEN 0 THEN "未结算" WHEN 1 THEN "已结算" WHEN 2 THEN "已审核" ELSE "---" END ) AS settleStatus, OC.CHANGE_REASON AS reason, ICE.CHANGE_REMARK AS remark, OC.CREATE_TIME AS orderTime, CASE WHEN ICE.CHANGE_TYPE = 1 THEN '废票' ELSE '退票' END AS refundType FROM OS_SALECHANGE AS OC LEFT JOIN OS_SALEORDER AS OSO ON ( OSO.SALE_ORDER_NO = OC.SALE_ORDER_NO AND OSO.VALID = 1 ) LEFT JOIN OS_SALECHANGE_EXTEND AS OCE ON ( OCE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO ) LEFT JOIN OS_SALEORDER_EXTEND AS OSOEX ON ( OSOEX.SALE_ORDER_NO = OSO.SALE_ORDER_NO ) LEFT JOIN OS_BUYORDER AS OBO ON ( OBO.BUSINESS_SIGN_NO = OSO.BUSINESS_SIGN_NO AND OBO.VALID = 1 ) LEFT JOIN OS_BUYORDER_EXTEND AS OBOE ON ( OBO.BUY_ORDER_NO = OBOE.BUY_ORDER_NO ) LEFT JOIN OS_CERTIFICATE AS CER ON ( CER.CERTIFICATE_NO = OCE.CERTIFICATE_NO ) LEFT JOIN IFT_SALE_CHANGE_EXT AS ICE ON ( ICE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO ) LEFT JOIN IFT_SALE_ORDER_EXT AS ISOE ON ( ISOE.SALE_ORDER_NO = OC.SALE_ORDER_NO ) LEFT JOIN CPS_CUSTOMER AS CUS ON ( OSO.CUSTOMER_NO = CUS.CUSTOMER_NO ) LEFT JOIN CPS_SUPPLIER AS CSP ON ( OBO.SUPPLIER_NO = CSP.SUPPLIER_NO ) LEFT JOIN SM_USER AS U1 ON (OSO.MODIFIER = U1.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT1 ON (DEPT1.ID = U1.DEPT_ID) LEFT JOIN SM_USER AS U2 ON (OC.MODIFIER = U2.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT2 ON (DEPT2.ID = U2.DEPT_ID) WHERE OC.GOODS_TYPE = 2 AND OC.ORDER_CHANGE_TYPE IN ('1','2') AND OC.CHILD_STATUS=10
AND OC.MODIFIER=#{refunder,jdbcType=VARCHAR}
AND OSO.MODIFIER=#{saler,jdbcType=VARCHAR}
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND DEPT1.ID=#{depId,jdbcType=VARCHAR}
AND DEPT2.ID=#{ticketDepId,jdbcType=VARCHAR}
AND OSO.SOURCE_CHANNEL_NO=#{sourceChannel,jdbcType=VARCHAR}
AND DATE_FORMAT(ISOE.ISSUE_TIME, '%Y-%m-%d') >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP}, '%Y-%m-%d')
AND DATE_FORMAT(ISOE.ISSUE_TIME, '%Y-%m-%d') <= DATE_FORMAT(#{endDate,jdbcType=TIMESTAMP}, '%Y-%m-%d')
AND ICE.CHANGE_TYPE =#{refundType,jdbcType=VARCHAR}
ORDER BY orderTime DESC

SELECT OBOE.SUPPLIER_NO_NAME AS supplierName, ICE.CHANGE_TYPE AS type, ISOE.ISSUE_TIME AS issueTime, OSO.SALE_ORDER_NO AS saleOrderNo, ISOD.TICKET_NO AS ticketNo, CONCAT(CONCAT(LEG.DEP_AIRPORT, '-'), LEG.ARR_AIRPORT) AS leg, IPR.SALE_PRICE AS salePrice, IPR.SALE_REFUND_PRICE AS refundPrice, IPR.SALE_BROKERAGE AS saleBrokerage, IPR.SALE_REFUND_ACCOUNT AS factRefundAccount, IPR.BUY_FEFUND_ACCOUNT AS buyRefundAccount, IPR.BUY_TAX AS tax, (IPR.BUY_FEFUND_ACCOUNT - IPR.BUY_TAX) AS settlePrice, (IFNULL(IPR.SALE_REFUND_ACCOUNT, 0) - IFNULL(IPR.BUY_FEFUND_ACCOUNT, 0)) AS depGrossProfit, (IFNULL(IPR.SALE_REFUND_ACCOUNT, 0) - IFNULL(IPR.BUY_FEFUND_ACCOUNT, 0)) AS grossProfit, OSO.MODIFIER AS saler, OC.MODIFIER AS refunder, DEPT1.NAME AS dep, DEPT2.NAME AS ticketDep, CUS.NAME AS customerName, CUS.BALANCE_CORPORATION AS customerCompany, OSO.SOURCE_CHANNEL_NO AS sourceChannel, CER.PAY_WAY AS settleWay, OSO.CUSTOMER_NO AS customerNo, (CASE CER.SETTLEMENT_STATUS WHEN 0 THEN "未结算" WHEN 1 THEN "已结算" WHEN 2 THEN "已审核" ELSE "---" END) AS settleStatus, OC.CHANGE_REASON AS reason, ICE.CHANGE_REMARK AS remark, CASE WHEN ICE.CHANGE_TYPE=1 THEN '废票' ELSE '退票' END AS refundType FROM OS_SALECHANGE AS OC LEFT JOIN OS_SALEORDER AS OSO ON (OSO.SALE_ORDER_NO = OC.SALE_ORDER_NO AND OSO.VALID = 1) LEFT JOIN OS_SALECHANGE_EXTEND AS OCE ON (OCE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN OS_SALEORDER_EXTEND AS OSOEX ON (OSOEX.SALE_ORDER_NO = OSO.SALE_ORDER_NO) LEFT JOIN OS_BUYORDER AS OBO ON (OBO.SALE_ORDER_NO = OSO.SALE_ORDER_NO AND OBO.VALID = 1) LEFT JOIN OS_BUYORDER_EXTEND AS OBOE ON (OBO.BUY_ORDER_NO = OBOE.BUY_ORDER_NO) LEFT JOIN OS_CERTIFICATE AS CER ON (CER.CERTIFICATE_NO = OCE.CERTIFICATE_NO) LEFT JOIN IFT_SALE_CHANGE_DETAIL AS ISCD ON (ISCD.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN IFT_SALE_CHANGE_EXT AS ICE ON (ICE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN IFT_SALE_ORDER_EXT AS ISOE ON (ISOE.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN IFT_SALE_ORDER_DETAIL AS ISOD ON (ISOD.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN IFT_PASSENGER_REFUND_PRICE AS IPR ON (IPR.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN CPS_CUSTOMER AS CUS ON (OSO.CUSTOMER_NO = CUS.CUSTOMER_NO) LEFT JOIN SM_USER AS U1 ON (OSO.MODIFIER = U1.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT1 ON (DEPT1.ID = U1.DEPT_ID) LEFT JOIN SM_USER AS U2 ON (OC.MODIFIER = U2.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT2 ON (DEPT2.ID = U2.DEPT_ID) LEFT JOIN IFT_LEG AS LEG ON (LEG.SALE_ORDER_NO = OSO.SALE_ORDER_NO) WHERE OC.GOODS_TYPE = 2 AND OC.ORDER_CHANGE_TYPE IN ('1','2')
AND OC.MODIFIER=#{refunder,jdbcType=VARCHAR}
AND OSO.MODIFIER=#{saler,jdbcType=VARCHAR}
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND DEPT1.ID=#{depId,jdbcType=VARCHAR}
AND DEPT2.ID=#{ticketDepId,jdbcType=VARCHAR}
AND OSO.SOURCE_CHANNEL_NO=#{sourceChannel,jdbcType=VARCHAR}
AND ISOE.ISSUE_TIME >=#{startDate,jdbcType=TIMESTAMP}
AND ISOE.ISSUE_TIME <=#{endDate,jdbcType=TIMESTAMP}
AND ICE.CHANGE_TYPE =#{refundType,jdbcType=VARCHAR}

SELECT OBOE.SUPPLIER_NO_NAME AS supplierName, ICE.CHANGE_TYPE AS type, ISOE.ISSUE_TIME AS issueTime, OSO.SALE_ORDER_NO AS saleOrderNo, ISOD.TICKET_NO AS ticketNo, CONCAT(CONCAT(LEG.DEP_AIRPORT, '-'), LEG.ARR_AIRPORT) AS leg, IPR.SALE_PRICE AS salePrice, IPR.SALE_REFUND_PRICE AS refundPrice, IPR.SALE_BROKERAGE AS saleBrokerage, IPR.SALE_REFUND_ACCOUNT AS factRefundAccount, IPR.BUY_FEFUND_ACCOUNT AS buyRefundAccount, IPR.BUY_TAX AS tax, (IPR.BUY_FEFUND_ACCOUNT - IPR.BUY_TAX) AS settlePrice, (IFNULL(IPR.SALE_REFUND_ACCOUNT, 0) - IFNULL(IPR.BUY_FEFUND_ACCOUNT, 0)) AS depGrossProfit, (IFNULL(IPR.SALE_REFUND_ACCOUNT, 0) - IFNULL(IPR.BUY_FEFUND_ACCOUNT, 0)) AS grossProfit, OSO.MODIFIER AS saler, OC.MODIFIER AS refunder, DEPT1.NAME AS dep, DEPT2.NAME AS ticketDep, CUS.NAME AS customerName, CUS.BALANCE_CORPORATION AS customerCompany, OSO.SOURCE_CHANNEL_NO AS sourceChannel, CER.PAY_WAY AS settleWay, OSO.CUSTOMER_NO AS customerNo, (CASE CER.SETTLEMENT_STATUS WHEN 0 THEN "未结算" WHEN 1 THEN "已结算" WHEN 2 THEN "已审核" ELSE "---" END) AS settleStatus, OC.CHANGE_REASON AS reason, ICE.CHANGE_REMARK AS remark, CASE WHEN ICE.CHANGE_TYPE=1 THEN '废票' ELSE '退票' END AS refundType FROM OS_SALECHANGE AS OC LEFT JOIN OS_SALEORDER AS OSO ON (OSO.SALE_ORDER_NO = OC.SALE_ORDER_NO AND OSO.VALID = 1) LEFT JOIN OS_SALECHANGE_EXTEND AS OCE ON (OCE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN OS_SALEORDER_EXTEND AS OSOEX ON (OSOEX.SALE_ORDER_NO = OSO.SALE_ORDER_NO) LEFT JOIN OS_BUYORDER AS OBO ON (OBO.SALE_ORDER_NO = OSO.SALE_ORDER_NO AND OBO.VALID = 1) LEFT JOIN OS_BUYORDER_EXTEND AS OBOE ON (OBO.BUY_ORDER_NO = OBOE.BUY_ORDER_NO) LEFT JOIN OS_CERTIFICATE AS CER ON (CER.CERTIFICATE_NO = OCE.CERTIFICATE_NO) LEFT JOIN IFT_SALE_CHANGE_DETAIL AS ISCD ON (ISCD.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN IFT_SALE_CHANGE_EXT AS ICE ON (ICE.SALE_CHANGE_NO = OC.SALE_CHANGE_NO) LEFT JOIN IFT_SALE_ORDER_EXT AS ISOE ON (ISOE.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN IFT_SALE_ORDER_DETAIL AS ISOD ON (ISOD.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN IFT_PASSENGER_REFUND_PRICE AS IPR ON (IPR.SALE_ORDER_NO = OC.SALE_ORDER_NO) LEFT JOIN CPS_CUSTOMER AS CUS ON (OSO.CUSTOMER_NO = CUS.CUSTOMER_NO) LEFT JOIN SM_USER AS U1 ON (OSO.MODIFIER = U1.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT1 ON (DEPT1.ID = U1.DEPT_ID) LEFT JOIN SM_USER AS U2 ON (OC.MODIFIER = U2.LOGIN_NAME) LEFT JOIN SM_DEPT AS DEPT2 ON (DEPT2.ID = U2.DEPT_ID) LEFT JOIN IFT_LEG AS LEG ON (LEG.SALE_ORDER_NO = OSO.SALE_ORDER_NO) WHERE OC.GOODS_TYPE = 2 AND OC.ORDER_CHANGE_TYPE IN ('1','2')
AND OC.MODIFIER=#{refunder,jdbcType=VARCHAR}
AND OSO.MODIFIER=#{saler,jdbcType=VARCHAR}
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND OC.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND OSO.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND DEPT1.ID=#{depId,jdbcType=VARCHAR}
AND DEPT2.ID=#{ticketDepId,jdbcType=VARCHAR}
AND OSO.SOURCE_CHANNEL_NO=#{sourceChannel,jdbcType=VARCHAR}
AND ISOE.ISSUE_TIME >=#{startDate,jdbcType=TIMESTAMP}
AND ISOE.ISSUE_TIME <=#{endDate,jdbcType=TIMESTAMP}
AND ICE.CHANGE_TYPE =#{refundType,jdbcType=VARCHAR}

SELECT DISTINCT pnr.PNR,sd.NAME AS saleDept, oc.SALE_CHANGE_NO, oc.ORDER_CHANGE_TYPE, oc.GOODS_TYPE, oc.CHANGE_REASON as REFUND_REASON, sod.TICKET_NO, CONCAT(iftpassenger.SURNAME,"/",iftpassenger.NAME) AS fltName, leg.DEP_TIME, iftpassenger.SALE_PRICE, passenger.SALE_TAX, passenger.SALE_REFUND_PRICE, iftpassenger.SALE_PRICE AS saleAccount, passenger.SALE_REFUND_ACCOUNT, cus.NAME AS customerName, soe.CREATOR AS intTicketNo, (SELECT smu.name FROM SM_USER smu WHERE smu.LOGIN_NAME =soe.CREATOR) AS intTicketName, soe.DRAWER_LOGIN_NAME AS outTicketNo, (SELECT smu2.name FROM SM_USER smu2 WHERE smu2.LOGIN_NAME =soe.DRAWER_LOGIN_NAME) AS outTicketName, soe.ISSUE_TIME, oc.CREATE_TIME AS refundTicketTime, oc.MODIFIER AS refundTicketNo, (SELECT smu3.name FROM SM_USER smu3 WHERE smu3.LOGIN_NAME =oc.MODIFIER) AS refundTicketName, pa.CREATE_TIME AS refundPriceTime, passenger.SALE_REFUND_ACCOUNT AS saleAccountPrice, ppw.NAME AS refundPayName, pa.RECEIVABLE_ACCOUNT AS refundAccount, sce.CHANGE_REMARK, iftpassenger.SALE_PRICE AS collectTicketPrice, sd2.NAME AS refundPriceDept, oc.SALE_ORDER_NO AS saleOrderNo FROM OS_SALECHANGE oc LEFT JOIN IFT_PNR pnr ON pnr.SOURCE_NO = oc.SALE_ORDER_NO LEFT JOIN IFT_SALE_ORDER_EXT soe ON soe.SALE_ORDER_NO = oc.SALE_ORDER_NO LEFT JOIN SM_USER su ON su.LOGIN_NAME = soe.CREATOR LEFT JOIN SM_DEPT sd ON sd.ID = su.DEPT_ID LEFT JOIN IFT_SALE_CHANGE_EXT sce ON sce.SALE_CHANGE_NO = oc.SALE_CHANGE_NO LEFT JOIN IFT_PASSENGER iftpassenger ON iftpassenger.SALE_ORDER_NO = oc.SALE_ORDER_NO LEFT JOIN OS_SALEORDER oso ON oso.SALE_ORDER_NO = oc.SALE_ORDER_NO LEFT JOIN IFT_SALE_ORDER_DETAIL sod ON sod.SALE_ORDER_NO = oso.SALE_ORDER_NO LEFT JOIN IFT_LEG AS leg ON leg.SALE_ORDER_NO = oso.SALE_ORDER_NO AND leg.PARENT_SECTION = 0 LEFT JOIN IFT_PASSENGER_REFUND_PRICE passenger ON passenger.SALE_CHANGE_NO = oc.SALE_CHANGE_NO LEFT JOIN CPS_CUSTOMER AS cus ON cus.CUSTOMER_NO = oso.CUSTOMER_NO LEFT JOIN OS_ACTUALAMOUNTRECORD oa ON oa.RECORD_NO = oc.SALE_CHANGE_NO LEFT JOIN PS_ALL_PAY_INFO pa ON pa.ALL_PAY_INFO_NO = oa.PAY_NO LEFT JOIN PS_PAY_WAY ppw ON ppw.CODE = pa.PAY_WAY_CODE LEFT JOIN SM_USER su2 ON su2.LOGIN_NAME = pa.COMPLETER LEFT JOIN SM_DEPT sd2 ON sd2.ID = su2.DEPT_ID WHERE oc.ORDER_CHANGE_TYPE IN ('1','2') AND oc.GOODS_TYPE = 2 AND oc.CHILD_STATUS=10
AND oc.MODIFIER=#{refunder,jdbcType=VARCHAR}
AND oso.MODIFIER=#{saler,jdbcType=VARCHAR}
AND oc.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND oso.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND oc.MODIFIER LIKE CONCAT(CONCAT('%', #{refunder}), '%')
AND oso.MODIFIER LIKE CONCAT(CONCAT('%', #{saler}), '%')
AND sd.ID=#{depId,jdbcType=VARCHAR}
AND sd2.ID=#{ticketDepId,jdbcType=VARCHAR}
AND oso.SOURCE_CHANNEL_NO=#{sourceChannel,jdbcType=VARCHAR}
AND DATE_FORMAT(soe.ISSUE_TIME, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
AND DATE_FORMAT(soe.ISSUE_TIME, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
AND sce.CHANGE_TYPE =#{refundType,jdbcType=VARCHAR}
ORDER BY oc.CREATE_TIME DESC
