			AND INSURE_TYPE = #{insureType,jdbcType=VARCHAR}
		
		
			AND INTERNAT_ORCIVIL = #{internatOrcivil,jdbcType=VARCHAR}
		
  	 
  	 ORDER BY CREATE_TIME ASC

