mapper.select:
select DISTINCT isoe.SALE_ORDER_NO, isoe.ID, isoe.OWNER, isoe.BUY_ORDER_NO, isoe.INSURANCE_NO, isoe.PROPOSAL_NO, isoe.ORDER_NO, isoe.ORDER_TYPE, isoe.POLICY_URL, isoe.POLICY_NO, isoe.TRANSACTION_ID, isoe.PRODUCT_KEY, isoe.DESTINATION, isoe.EFFECT_DATE, isoe.EXPIRE_DATE, isoe.ISSUE_DATE, isoe.HOLDER_TYPE, isoe.HOLDER_NAME, isoe.HOLDER_CERTI_TYPE, isoe.HOLDER_CERTI_NO, isoe.HOLDER_SEX, isoe.HOLDER_BIRTHDAY, isoe.HOLDER_EMAIL, isoe.HOLDER_PHONE, isoe.CUSTOMER_TYPE_NO, isoe.SALE_PRICE, isoe.BUY_PRICE, isoe.TOTAL_PREMIUM, isoe.TRAVEL_DAY, isoe.INSURED_COUNT, isoe.REMARK, isoe.IS_TEAM, isoe.SOURCE_NAME, isoe.EXTENDED_FIELDS_JSON, isoe.PNR, isoe.CREATE_TIME, isoe.MODIFY_TIME, isoe.CREATOR, isoe.MODIFIER, isoe.VALID, si.NAME, cc.NAME AS CUSTOMER_NAME, cct.NAME AS CUSTOMER_TYPE_NAME FROM INS_SALE_ORDER_EXT isoe, INS_INSURANCE ii, INS_SALE_ORDER_DETAIL isod, OS_SALEORDER os, SM_OWNER_INFO si, CPS_CUSTOMER_TYPE cct, CPS_CUSTOMER cc where isoe.VALID = 1 and os.PAY_STATUS != 1 and isod.STATUS != 1 and isod.STATUS != 3 and isoe.SALE_ORDER_NO = os.SALE_ORDER_NO and isoe.INSURANCE_NO = ii.INSURANCE_NO and isoe.SALE_ORDER_NO = isod.SALE_ORDER_NO and isoe.OWNER = si.OWNER and cct.CUSTOMER_TYPE_NO = os.CUSTOMER_TYPE_NO and cc.CUSTOMER_NO = os.CUSTOMER_NO
AND isoe.OWNER = #{owner,jdbcType=INTEGER}
AND os.CUSTOMER_NO in
${lowerCustomers.customerNo}
AND isoe.CREATOR = #{creator,jdbcType=VARCHAR}
AND isoe.HOLDER_NAME LIKE CONCAT(CONCAT('%', #{holderName}), '%')
AND isoe.PROPOSAL_NO = #{proposalNo,jdbcType=INTEGER}
AND isoe.SALE_ORDER_NO = #{saleOrderNo,jdbcType=VARCHAR}
AND isoe.HOLDER_PHONE LIKE CONCAT(CONCAT('%', #{holderPhone}), '%')
AND isoe.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
AND isod.POLICY_NO = #{policyNo,jdbcType=VARCHAR}
AND isoe.PNR = #{pnr,jdbcType=VARCHAR}
AND date(isoe.ISSUE_DATE) = date(#{issueDate,jdbcType=TIMESTAMP})
AND date(isoe.EFFECT_DATE) = date(#{effectDate,jdbcType=TIMESTAMP})
AND ii.INSURE_TYPE = #{insureType,jdbcType=VARCHAR}
AND date(isoe.MODIFY_TIME) >= date(#{refundStartDate,jdbcType=DATE})
AND date(isoe.MODIFY_TIME) <= date(#{refundEndDate,jdbcType=DATE})
AND ii.NAME LIKE CONCAT(CONCAT('%', #{insuranceName}), '%')
AND isod.INSURED_NAME LIKE CONCAT(CONCAT('%', #{insuredName}), '%')
AND os.PAY_STATUS = #{payStatus,jdbcType=VARCHAR}
AND os.TRANSATION_ORDER_NO = #{transactionOrderNo,jdbcType=VARCHAR}
AND os.ORDER_CHILD_STATUS = #{orderChildStatus,jdbcType=TINYINT}
AND date(isoe.ISSUE_DATE) >= date(#{issueStartDate,jdbcType=DATE})
AND date(isoe.ISSUE_DATE) <= date(#{issueEndDate,jdbcType=DATE})
AND os.CUSTOMER_TYPE_NO = #{customerTypeNo,jdbcType=INTEGER}
AND os.CUSTOMER_NO = #{customerNo,jdbcType=INTEGER}
AND date(isoe.EFFECT_DATE) >= date(#{startEffectTime,jdbcType=DATE})
AND date(isoe.EFFECT_DATE) <= date(#{endEffectTime,jdbcType=DATE})
AND date(isoe.MODIFY_TIME) >= date(#{refundStartDate,jdbcType=DATE})
AND date(isoe.MODIFY_TIME) <= date(#{refundEndDate,jdbcType=DATE})
AND date(isoe.ISSUE_DATE) >= date(#{startInsureTime,jdbcType=DATE})
AND date(isoe.ISSUE_DATE) <= date(#{endInsureTime,jdbcType=DATE})
AND isoe.TRANSACTION_ID = #{transactionId,jdbcType=VARCHAR}
ORDER BY CREATE_TIME DESC
