mapper.select:
  	 select 

     from UNP_SUPPLIER 
     where  VALID=1


			AND SUPPLIER_NO = #{supplierNo,jdbcType=BIGINT}


      	 	 AND STATUS = #{status,jdbcType=INTEGER}


       		AND CREATOR = #{creator,jdbcType=VARCHAR}


       	AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


  	 	ORDER BY SORT ASC
