mapper.select:
SELECT ub.SALE_ORDER_NO AS SALE_ORDER_NO, cs.SHORT_NAME AS SUPPLIER_NAME, ue.SOURCE AS SOURCE, upn.`NAME` AS PRODUCT_NAME, ubi.ITEM_STATUS AS ITEM_STATUS, mt.`NAME` AS DEPT_NAME, ue.ACTUAL_AMOUNT AS SALE_ACTUAL_AMOUNT, ub.PLAN_AMOUNT AS BUY_PLAN_AMOUNT, ub.ACTUAL_AMOUNT AS BUY_ACTUAL_AMOUNT, ub.CREATE_TIME AS CREATE_TIME, ui.ADDITIONAL_INFO AS ADDITIONAL_INFO, ub.THIRD_BUS_NO AS THIRD_BUS_NO FROM UNP_BUY ub LEFT JOIN CPS_SUPPLIER cs ON ub.SUPPLIER_ID = cs.SUPPLIER_NO LEFT JOIN UNP_SALE ue ON ue.SALE_ORDER_NO = ub.SALE_ORDER_NO LEFT JOIN UNP_BUY_ITEM ubi ON ubi.BUY_ORDER_NO = ub.BUY_ORDER_NO LEFT JOIN UNP_ITEM_TYPE upn ON upn.ITEM_TYPE_NO = ubi.UNP_TYPE LEFT JOIN SM_USER sr ON sr.LOGIN_NAME = ub.CREATOR LEFT JOIN SM_DEPT mt ON mt.ID = sr.DEPT_ID LEFT JOIN UNP_SALE_ITEM ui ON ui.SALE_ORDER_NO = ub.SALE_ORDER_NO WHERE ui.ITEM_STATUS IN (3, 4, 5) AND ub.VALID = 1
AND ub.CREATE_TIME >=CONCAT(#{ticketDateStart},' 00:00:00')
AND ub.CREATE_TIME <=CONCAT(#{ticketDateEnd},' 23:59:59')
AND upn.ITEM_TYPE_NO =#{itemTypeNo}
AND ubi.CHANGE_TYPE=#{changeType}
ORDER BY ub.SALE_ORDER_NO
